package javaSessions;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//single for loop
		for(int i = 0; i<=10; i++) {
			System.out.println(i);
		}
		//nested for loop - 2
		for(int i = 0; i<=5; i++) {
			for(int j = 0; j<=5; j++) {
				System.out.println(i+""+j);
			}
			
		}

	}

}
